import React, { useCallback, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const BankPayment = ({ clearCart }) => {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  // Mock list of banks with names and image URLs
  const banks = [
    { id: 'bank1', name: 'Bank of America', image: 'data:image/png;base64,' },
    { id: 'bank2', name: 'Chase Bank', image: 'data:image/png;base64,' },
    { id: 'bank3', name: 'Wells Fargo', image: 'data:image/png;base64,' },
    { id: 'bank4', name: 'Citibank', image: 'data:image/png;base64,' },
    { id: 'bank5', name: 'TD', image: 'data:image/png;base64,' },
    { id: 'bank6', name: 'BMO', image: 'data:image/png;base64,' },
    { id: 'bank7', name: 'CIBC', image: 'data:image/jpeg;base64,' },
    { id: 'bank8', name: 'US Bank', image: 'data:image/png;base64,' }
  ];

  const paymentOptions = [
    { id: 'paybybank', label: 'Pay by Bank' },
    { id: 'paybycard', label: 'Pay by Crypto Wallet' },
    { id: 'paybyemail', label: 'Pay by Card' },
    { id: 'paybycrypto', label: 'Pay by Email/Mobile (Zelle & Venmo)' },
  ];

  const handleBankLoginPopup = useCallback((bank) => {
    const popup = window.open(
      '/bank-login.html?bank=' + bank,
      '_blank',
      'width=600,height=700'
    );

    const receiveMessage = (event) => {
      if (event.origin !== window.location.origin) return;
      if (event.data?.type === 'BANK_PAYMENT_SUCCESS') {
        clearCart();
        navigate('/confirmation', {
          state: { email: event.data.email || 'customer@example.com' }
        });
        window.removeEventListener('message', receiveMessage);
        popup.close();
      }
    };

    window.addEventListener('message', receiveMessage);
  }, [clearCart, navigate]);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Payment Options</h2>
  
      {/* Back to Cart Button */}
      <button
        className="btn btn-secondary mb-3"
        onClick={() => navigate('/cart')}
      >
        Back to Cart
      </button>
  
      {!selectedOption ? (
        <div className="row">
          {paymentOptions.map((option) => (
            <div key={option.id} className="col-6 text-center">
            {/* Image above the button */}
            <div className="mb-2">
              <img
                src={
                  option.id === 'paybybank'
                    ? 'data:image/png;base64,' // Bank image
                    : option.id === 'paybyemail'
                    ? 'data:image/png;base64,' // Replace with Email/Mobile image
                    : option.id === 'paybycrypto'
                    ? 'data:image/png;base64,' // Replace with Crypto Wallet image
                    : 'data:image/jpeg;base64,' // Default image
                }
                alt={option.label}
                style={{ width: '200px', height: '200px' }}
              />
            </div>
            <button
              className="btn btn-primary w-100 mb-3"
              onClick={() => {
                if (option.id === 'paybybank') {
                  setShowModal(true); // Show the modal for banks
                } else {
                  setSelectedOption(option.id);
                }
              }}
            >
              {option.label}
            </button>
          </div>
          ))}
        </div>
      ) : selectedOption === 'paybycard' ? (
        <div>
          <p>Card payment is not implemented yet.</p>
          <button
            className="btn btn-secondary"
            onClick={() => setSelectedOption(null)}
          >
            Back to Payment Options
          </button>
        </div>
      ) : null}
  
      {showModal && (
        <div className="modal fade show d-block" tabIndex="-1" role="dialog">
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Select Your Bank</h5>
                <button className="close" onClick={() => setShowModal(false)}>
                  <span>&times;</span>
                </button>
              </div>
              <div className="modal-body row">
                {banks.map((bank) => (
                  <div
                    key={bank.id}
                    className="col-6 text-center"
                    onClick={() => {
                      handleBankLoginPopup(bank.id);
                      setShowModal(false);
                    }}
                  >
                    <div className="p-4 border rounded shadow-sm">
                      <img
                        src={bank.image}
                        alt={bank.name}
                        className="img-fluid mb-2"
                        style={{ maxHeight: '80px' }}
                      />
                      <h5>{bank.name}</h5>
                    </div>
                  </div>
                ))}
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowModal(false)}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
export default BankPayment;